const API_KEY = 'b41ebc557f327c6c95cd8ab3ce1b25f6'
const API_ENDPOINT = `https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${API_KEY}&format=json&text=a&safe_search=3&bbox=-50,-30,50,30`

export async function fetchImageUrls() {

    const rawImages = await fetchRawImages()

    return rawImages.map(image => urlFromProperties(image.farm, image.server, image.id, image.secret))
}

async function fetchRawImages() {
    const response = await fetch(API_ENDPOINT)

    //Bad response status
    const rawJson = await response.text()

    const imagesJson = JSON.parse(rawJson.slice(14, -1))

    return imagesJson.photos.photo;
}


function urlFromProperties(farmId, serverId, id, secret) {
    return `https://farm${farmId}.staticflickr.com/${serverId}/${id}_${secret}_q.jpg`
}


function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
}
