import React from 'react';
import './App.css';

const API_KEY = 'b41ebc557f327c6c95cd8ab3ce1b25f6'

function App() {
  const [images, setImages] = React.useState([]);

  React.useEffect(() => {
    setImages(['2', '10', '20', '25'])
    fetchImageUrls().then(imageUrls => setImages(imageUrls))
  }, [])



  return (
    <div className='container'>
      <div className='row justify-content-center mt-4'>Images</div>
      <div className='row mt-5'>{images.map(image => <div className='col col-md-3 mt-4'><img crossOrigin='anonymous' src={image}></img></div>)}</div>
    </div>
  );
}

export default App;

async function fetchRawImages() {
  // const response = await fetch(`https://www.flickr.com/services/rest/?method=flickr.photos.getRecent&api_key=${API_KEY}&format=json`)
  const response = await fetch(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${API_KEY}&format=json&text=b&safe_search=3`)
  const rawJson = await response.text()

  const imagesJson = JSON.parse(rawJson.slice(14, -1))

  return imagesJson.photos.photo;
}


function urlFromProperties(farmId, serverId, id, secret) {
  return `https://farm${farmId}.staticflickr.com/${serverId}/${id}_${secret}_q.jpg`
}

async function fetchImageUrls() {
  const rawImages = await fetchRawImages()

  return rawImages.map(image => urlFromProperties(image.farm, image.server, image.id, image.secret))
}
