import React from 'react';
import './App.css';
import fetchImageUrls from './imageUtils';

function loadImageSources(setImageSources) {
  fetchImageUrls().then((imageUrls) => setImageSources(imageUrls));
}

function App() {
  const [imageSources, setImageSources] = React.useState([]);

  React.useEffect(() => {
    loadImageSources(setImageSources);
  }, []);

  const refreshImages = React.useCallback(
    () => loadImageSources(setImageSources), [setImageSources],
  );


  return (
    <>
      <div className="container">
        <div className="row mt-4 justify-content-center">
          <h2>Randomized Images</h2>
        </div>
        <div className="row mt-4 justify-content-center">
          <button type="submit" className="btn-primary" onClick={refreshImages}>
            Refresh
          </button>
        </div>
        <div className="row mt-4">
          {imageSources.map((imageSource) => (
            <div className="col col-md-3 mt-4">
              <img alt={imageSource} crossOrigin="anonymous" src={imageSource} />
            </div>
          ))}
        </div>
      </div>
    </>
  );
}


export default App;
